INSERT INTO `helpdocs` (`hid`, `sid`, `name`, `description`, `document`, `usetranslation`, `enabled`, `disporder`) VALUES
(1, 1, 'Enregistrement de l''utilisateur', 'Avantages et privilèges de l''enregistrement.', 'Quelques parties de ce forum peuvent demander une inscription, et de vous connecter. L''enregistrement est gratuit et prend seulement quelques minutes.<br />\r\n<br />\r\nVous êtes encouragé à vous enregistrer ; une fois enregistré, vous pourrez poster des messages, définir vos préférences, et mettre à jour votre profil.<br />\r\n<br />\r\nLes éléments qui demandent un enregistrement sont les abonnements, la gestion des favoris, le changement de style et de thème, l''accès au bloc-notes personnel et le contact par mail des membres du forum.', 1, 1, 1),
(2, 1, 'Mise à jour du profil', 'Modifier vos données enregistrées.', 'A certains moments, vous pourrez décider de mettre à jour certaines informations, comme par exemple les informations de messagerie instantanée, votre mot de passe, ou encore le changement d''adresse email. Vous pouvez changer ces informations depuis le panneau de configuration de l''utilisateur. Pour accéder à ce panneau, cliquez simplement sur le lien en haut de la page. Depuis cet endroit, choisissez de modifier votre profil et changez ou mettez à jour toutes les données que vous désirez, validez ensuite vos changements avec le bouton adéquat en bas de la page pour qu''ils prennent effet.', 1, 1, 2),
(3, 1, 'Usage des cookies avec MyBB', 'MyBB utilise des cookies et conserve certaines informations sur votre connexion.', 'MyBulletinBoard utilise les cookies pour stocker vos informations de connexion, si vous êtes enregistré, et votre dernière visite, si vous ne l''êtes pas.<br />\r\n<br />\r\nLes cookies sont des petits documents de texte stockés sur votre ordinateur ; les cookies de ce forum ne sont utilisés que par ce forum et ne posent aucun problème de sécurité.<br />\r\n<br />\r\nLes cookies de ce forum suivent vos lectures et le moment où vous lisez certains messages.<br />\r\n<br />\r\nPour supprimer tous les cookies de ce forum, vous pouvez cliquer <a href="misc.php?action=clearcookies&amp;key={1}">ici</a>.', 1, 1, 3),
(4, 1, 'Connexion et déconnexion', 'Comment se connecter et se déconnecter.', 'Quand vous vous connectez, vous créez un cookie sur votre ordinateur, ce qui vous permet de visiter le forum sans avoir à redonner votre nom d''utilisateur et votre mot de passe à chaque fois. La déconnexion supprime les cookies, pour s''assurer que personne ne navigue sur le forum avec votre compte.<br />\r\n<br />\r\nPour vous connecter, cliquez simplement sur le bouton se connecter en haut. Pour vous déconnecter, cliquez sur le bouton qui est à la même position. Enfin, si vous supprimez les cookies de votre ordinateur, vous serez déconnecté.', 1, 1, 4),
(5, 2, 'Poster un nouveau sujet', 'Créer un nouveau sujet dans un forum.', 'Quand vous êtes intéressé par un forum, vous pouvez créer un nouveau sujet, choisissez simplement le bouton situé en haut et en bas intitulé "Nouveau sujet". N''oubliez pas que vous n''aurez peut-être pas les permissions de poster dans tous les forums, en fonction des restrictions imposées par l''Administration du forum.', 1, 1, 1),
(6, 2, 'Poster une réponse', 'Répondre à un message.', 'Vous pouvez répondre à un message, dans un sujet. Pour faire ceci, cliquez sur le bouton situé en haut et en bas intitulé "Répondre". N''oubliez pas que vous n''aurez peut-être pas les permissions de poster dans tous les forums, en fonction des restrictions imposées par l''Administration du forum.<br />\r\n<br />\r\nEn outre, un modérateur d''un forum peut verrouiller un sujet, ce qui fait qu''il est impossible à poster à la suite. Un utilisateur ne peut pas réouvrir un sujet verrouillé sans l''intervention d''un modérateur ou d''un administrateur.', 1, 1, 2),
(7, 2, 'MyCode', 'Utiliser le MyCode, pour agrémenter vos messages.', 'Vous pouvez utiliser le MyCode, une version simplifiée du HTML, dans vos messages pour créer certains effets..\r\n<p><br />\r\n[b]Ce texte est gras[/b]<br />\r\n&nbsp;&nbsp;&nbsp;<b>Ce texte est gras</b>\r\n<p>\r\n[i]Ce texte est en italique[/i]<br />\r\n&nbsp;&nbsp;&nbsp;<i>Ce texte est en italique</i>\r\n<p>\r\n[u]Ce texte est souligné[/u]<br />\r\n&nbsp;&nbsp;&nbsp;<u>Ce texte est souligné</u>\r\n<p><br />\r\n[s]Ce texte est barré[/s]<br />\r\n&nbsp;&nbsp;&nbsp;<strike>Ce texte est barré</strike>\r\n<p><br />\r\n[url]http://www.example.com/[/url]<br />\r\n&nbsp;&nbsp;&nbsp;<a href="http://www.example.com/">http://www.example.com/</a>\r\n<p>\r\n[url=http://www.example.com/]Example.com[/url]<br />\r\n&nbsp;&nbsp;&nbsp;<a href="http://www.example.com/">Example.com</a>\r\n<p>\r\n[email]example@example.com[/email]<br />\r\n&nbsp;&nbsp;&nbsp;<a href="mailto:example@example.com">example@example.com</a>\r\n<p>\r\n[email=example@example.com]Envoyez moi un mail ![/email]<br />\r\n&nbsp;&nbsp;&nbsp;<a href="mailto:example@example.com">Envoyez moi un mail !</a>\r\n<p>\r\n[email=example@example.com?subject=spam]E-mail avec objet[/email]<br />\r\n&nbsp;&nbsp;&nbsp;<a href="mailto:example@example.com?subject=spam">E-mail avec objet</a>\r\n<p><br />\r\n[quote]Texte cité[/quote]<br />\r\n&nbsp;&nbsp;&nbsp;<quote>Texte cité</quote>\r\n<p>\r\n[code]Texte avec formatage préservé[/code]<br />\r\n&nbsp;&nbsp;&nbsp;<code>Texte avec formatage préservé</code>\r\n<p><br />\r\n[img]http://www.php.net/images/php.gif[/img]<br />\r\n&nbsp;&nbsp;&nbsp;<img src="http://www.php.net/images/php.gif">\r\n<p>\r\n[img=50x50]http://www.php.net/images/php.gif[/img]<br />\r\n&nbsp;&nbsp;&nbsp;<img src="http://www.php.net/images/php.gif" width="50" height="50">\r\n<p><br />\r\n[color=red]Ce texte est rouge[/color]<br />\r\n&nbsp;&nbsp;&nbsp;<font color="red">Ce texte est rouge</font>\r\n<p>\r\n[size=3]Ce texte est de taille 3[/size]<br />\r\n&nbsp;&nbsp;&nbsp;<font size="3">Ce texte est de taille 3</font>\r\n<p>\r\n[font=Tahoma]Cette police est Tahoma[/font]<br />\r\n&nbsp;&nbsp;&nbsp;<font face="Tahoma">Cette police est Tahoma</font>\r\n<p><br />\r\n[align=center]C''est centré[/align]<div align="center">C''est centré</div>\r\n<p>\r\n[align=right]C''est aligné à droite[/align]<div align="right">C''est aligné à droite</div>\r\n<p><br />\r\n[list]<br />\r\n[*]Objet de la liste #1<br />\r\n[*]Objet de la liste #2<br />\r\n[*]Objet de la liste #3<br />\r\n[/list]<br />\r\n<ul>\r\n<li>Objet de la liste #1</li>\r\n<li>Objet de la liste #2</li>\r\n<li>Objet de la liste #3</li>\r\n</ul><p><font size=1>Vous pouvez faire une liste hiérarchisée en utilisant [list=1] pour une numérotation, et [list=a] pour un classement alphabétique.</size>', 1, 1, 3);

